int ctoi(char c)
{
if ((c >= '0') && (c <= '9'))
return c - '0';

return -1;
}

long find(char string[], int length)
{
  long i=0,j=0,sum=0;
  long max=0;
  for(i=0; i<strlen(string)-length; i++)
    {
      sum=1;
      for(j=0; j<length; j++){
        sum*=ctoi(string[i+j]);
      }
      if(sum>max){
        max = sum;
      }
        
    }
  
  return max;
}

